class Solution {
    private Integer[][] memo;
    public int minCut(String s) {
        memo = new Integer[s.length()][s.length()];
        for (int i=0; i<s.length(); i++)
            Arrays.fill(memo[i], null);
        return minCut(s, 0, s.length()-1);
    }
    private int minCut(String s, int left, int right) {
        if (left == right) return 0;
        if (right - left == 1)
            return s.charAt(right) == s.charAt(left) ? 0 : 1;
        if (memo[left][right] != null) 
            return memo[left][right];
        int ans = Integer.MAX_VALUE;
        if (s.charAt(left) == s.charAt(right)) {
            ans = minCut(s, left+1, right-1);
            ans = ans == 0 ? 0 : ans + 2;
        }
        for (int i=left; i<=right-1; i++) {
            ans = Math.min(ans, minCut(s, left, i) + minCut(s, i+1, right) + 1);
        }
        memo[left][right] = ans;
        return ans;
    }
}
