# Code
```
class Solution {
public:

    bool pal(string &a, int i, int j){
        while(i<=j){
            if(a[i]!=a[j]) return false;
            i++, j--;
        }
        return true;
    }

    //  pre calculation of string to be palindrome for all i and j
    void preCalculate(vector<vector<int>> &ispal, string &s){
        ispal[0][0] = 1;
        for(int i = 1; i<s.size(); i++){
            if(pal(s, 0, i)){
                ispal[0][i] = 1;
                ispal[i][0] = 1;
            }
            ispal[i][i] = 1;
        }
        for(int i = s.size()-1; i>=0; i--){
            for(int j = 1; j<s.size(); j++){
                if(s[i]==s[j] && (j-i<2 || ispal[i+1][j-1])){
                    ispal[i][j] = 1;
                }
            }
        }
        return;
    }


    int minCut(string s) {
        vector<vector<int>> ispal(s.size()+1, vector<int>(s.size()+1, 0));
        preCalculate(ispal, s);
        vector<int> dp(s.size()+1, 0);

        // Tabulation
        for(int i = s.size()-1; i>=0; i--){
            int ans = INT_MAX;
            for(int k = i; k<s.size(); k++){
                if(ispal[i][k]){
                    int x = 1+dp[k+1];
                    ans = min(ans, x);
                }
            }
            dp[i] = ans;
        }
        return dp[0]-1;
    }
};
```

