class Solution {
public:
    int minCut(string s) {
        int n = s.length();
        vector<vector<int>> d = vector<vector<int>>(n+1, vector<int>(n+1,0));
        d[n][n] = 1;

        for(int i = n-1; i >= 0; i--){
            d[i][i] = 1;
            for(int j = i+1; j < n; j++){
                if(s[i] == s[j]){
                    if(i == j-1){
                        d[i][j] = 1;
                    }
                    else{
                        d[i][j] = d[i+1][j-1];
