class Solution {
public:

    string str;
    vector<vector<int>> p;
    vector<int> dp;

    bool isPalindrome(int s,int e)
    {
        if(s >= e)
        {
            return true;
        }
        if(p[s][e] != -1)
        {
            return p[s][e];
        }
        if(str[s] != str[e])
        {
            return false;
        }

        return p[s][e] = isPalindrome(s+1,e-1);
    }

    int minCutsHelper(int s,int e)
    {
        if(s == e)
        {
            return 0;
        }

        if(dp[s] != -1)
        {
            return dp[s];
        }

        int minCount = INT_MAX;
        for(int j = s;j<e;j++)
        {
            if(isPalindrome(s,j))
            {
                int count = 1 + minCutsHelper(j+1,e);
                minCount = min(minCount,count);
            }  
        }
        return dp[s] = minCount;
    }

    int minCut(string s) 
    {
        this->str = s;
        int n = s.size();
        p.resize(n,vector<int>(n,-1));
        dp.resize(n,-1);
        return minCutsHelper(0,n) - 1;
    }
};
```

