class Solution {
public:
    int minCut(string s) {
        int n = s.length();
        vector<int> dp(n+1,n-1);
        dp[0] = -1;
        for(int i=0;i<n;i++)
        {
            //odd length
            int l = i , r = i;
            while(l>=0 and r<n and s[l] == s[r])
            {
                dp[r+1] = min(dp[r+1],1+dp[l]);
                l--;
                r++;
            }
            //even length
            l = i , r = i+1;
            while(l>=0 and r<n and s[l] == s[r])
            {
                dp[r+1] = min(dp[r+1],1+dp[l]);
                l--;
                r++;
            }
        }
        return dp[n];
    }
};
/*   for string 0-1-2--------8 if (2-8) is palindrome then the no of partitions will equal no of
     cuts in string(0-1) + 1(cut) + 0(as 2-8 is palindrome)*/

//O(n^2) time and O(n) space
