class Solution {
    int dp[][]=new int[2001][2001];
    public int minCut(String s) {
        int n=s.length();
        for(int row[]: dp)
            Arrays.fill(row, -1);
        return solve(s, 0, n-1);
    }
    public boolean isPalindrome(String s, int i, int j)
    {
        if(i>=j)
            return true;
        while(i<j)
        {
            if(s.charAt(i)!=s.charAt(j))
                return false;
            i++;
            j--;
        }
        return true;
    }
    public int solve(String s, int i, int j)
    {

        if(i>=j) return 0;

        if(isPalindrome(s,i,j)==true)
            return 0;

        int mn= Integer.MAX_VALUE;

        if(dp[i][j]!=-1)
            return dp[i][j];

        for(int k = i; k < j; k++)
        {
    // if substring of str in range [i, k] is a palindrome, then check the range[k+1, j]
           if(isPalindrome(s, i , k))
           {
   // cutting the string from i to k will take 1 cut and from k+1 to j will be returned by the function.
               int temp_ans = 1 + solve(s, k+1, j);
               mn = Math.min(mn, temp_ans);
            }
         }
        //Because we can check directly that if the first partition is Palindrome we can check for the rest k+1 to j partition but if the first i to k partition is not Palindrome. You need not check the next k+1 to j.
        return dp[i][j]=mn;
    }

}
