class Solution {
public:
    int dp[2001];
    int pal[2001][2001];
    bool isPalindrome(int i,int j){
        return pal[i][j];
    }
    int Solve(int i , string &s, int n){
        if(i >= n){
            return 0;
        }
        if(dp[i] != -1){
            return dp[i];
        }
        int ans = INT_MAX;
        for(int j = i; j < n; j++){
            // string curr = s.substr(i,j-i+1);
            if(isPalindrome(i,j)){
                ans = min(ans,Solve(j+1,s,n));
            }
        }

        dp[i] = (ans == INT_MAX) ? ans : ans + 1;
        return dp[i];
    }
    int minCut(string s) {
        memset(dp,-1,sizeof(dp));
        int n = s.length();
        for(int i = n-1; i >= 0; i--){
            for(int j = 0; j < n; j++){
                if(i > j){
                    pal[i][j] = 0;
                }
                else if(i == j){
                    pal[i][j] = 1;
                }
                else{
                    if(s[i] == s[j]){
                        pal[i][j] = i+1 == j ? 1 : pal[i+1][j-1];
                    }
                    else{
                        pal[i][j] = 0;
                    }
                }
            }
        }

        return Solve(0,s,s.length())-1;
    }
};
