class Solution {
public:
    int minCut(string s) {
        int n = s.length();
        bool dp[n][n];
        for(int i = 0;i<n;i++)
            dp[i][i] = true;
        for(int g = 1;g<n;g++)
        {
            for(int i=0,j=g;j<n;i++,j++)
            {
                if(g == 1)
                {
                    dp[i][j] = s[i]==s[j];
                }
                else
                {
                    if(s[i] == s[j] and dp[i+1][j-1] == true)
                        dp[i][j] = true;
                    else
