class Solution {
    public int minCut(String s) {
        if(ispalindrome(s.toCharArray(),0, s.length()-1)) {
            return 0;
        }
        int[] dp = new int[s.length()+1];
        Arrays.fill(dp, -1);
        int min = dfs(s.toCharArray(), 0, dp);

        return min;
    }

    int dfs(char[] s, int index, int[] dp) {
        if(index==s.length) { 
            return 0;
        }

        if(dp[index]!=-1) return dp[index];

        if(ispalindrome(s, index, s.length-1)) {
            dp[index] = 0;
            return 0;
        }
        int min = s.length;

        for(int i=index;i<s.length;i++) {
            if(ispalindrome(s, index, i)) {
                min = Math.min(min, dfs(s, i+1, dp)+1);

                if(min==1) break;
            }
        }
        dp[index] = min;
        return min;
    }

    boolean ispalindrome(char[] arr, int i, int j) {
        while(i<=j) {
            if(arr[i]!=arr[j]) return false;
            i++; j--;
        }
        return true;
    }
}
