        int len = s.size();
        vector<vector<int> > isPal(len, vector<int>(len, 0));
        vector<int> dp(len);

        for(int i=len-1;i>=0;i--){
            dp[i] = len - 1 - i; // a baseline min cut
            for(int j=i;j<len;j++){
                if(s[j]==s[i]){
                    if(j-i<2 || isPal[i+1][j-1]){ // (i,j) must be a pal string
                        isPal[i][j] = 1;
                        if(j==len-1) dp[i] = 0;
                        else dp[i] = min( dp[i], dp[j+1]+1);
                    }
                }
            }
        }
        return dp[0];
    }
