class Solution {
    public int minCut(String s) {  
    int n = s.length();
        int[][] dp = new int[n][n];
        for(int[] d : dp){
            Arrays.fill(d,-1);
        }
        return minCut(s, 0, n-1,dp);
    }

    private int minCut(String s, int i, int j,int[][] dp){
        if(i >= j)
            return 0;


        if(dp[i][j] != -1){
            return dp[i][j];
        }
        if(isPalindrome(s,i,j)){
            dp[i][j] = 0;
            return 0;
        }
         int left = 0, right = 0;
        int ans = Integer.MAX_VALUE;
        for(int k = i;k<=j-1;k++){
            if(dp[i][k] != -1){
                 left = dp[i][k];
            }else {
                left = minCut(s,i,k,dp);
                dp[i][k] = left;
            }
            if(dp[k+1][j] != -1){
                right = dp[k+1][j];
            }else {
                right = minCut(s,k+1,j,dp);
                dp[k+1][j] = right;
            }
            int tempAns =1+left+right;
            ans = Math.min(ans,tempAns);
        }
        return dp[i][j] = ans;
    }

    private boolean isPalindrome(String s, int i, int j){
        while(i<=j){
            if(s.charAt(i) != s.charAt(j)){

                return false;
            }
            i++;
            j--;
        }
        return true;
    }
}
