class Solution {
    public boolean isPalindrome(String s, int i, int j) {
        while (i < j) {
            if (s.charAt(i++) != s.charAt(j--)) {
                return false;
            }
        }
        return true;
    }

    public int minCut(String s) {
        if (isPalindrome(s, 0, s.length()-1)) return 0;
        return dfs(s, 0);
    }

    HashMap<Integer, Integer> memo = new HashMap<>();
    public int dfs(String s, int idx) {
        if (idx >= s.length() || isPalindrome(s, idx, s.length()-1)) {
            return 0;
        }
        if (memo.containsKey(idx)) {
            return memo.get(idx);
        }

        int result = Integer.MAX_VALUE;
        for (int i = idx+1; i <= s.length(); i++) {
            if (i != s.length() && isPalindrome(s, idx, i-1)) {
                result = Math.min(result, 1 + dfs(s, i));
            }
        }
        result = (result == Integer.MAX_VALUE)? 0 : result;
        memo.put(idx, result);
        return result;
    }
}
