class Solution:
    def catMouseGame(self, graph: List[List[int]]) -> int:

        @functools.lru_cache(None)
        def helper(turn, cat, mouse):

            if turn == 0: return 0
            options = set()

            # Cat's turn
            if turn & 1:
                if mouse in graph[cat] or cat == mouse: return 2
                for c in graph[cat] - {0}:
                    options.add(helper(turn - 1, c, mouse))
                    if 2 in options: return 2
                return min(options)

            # Mouse's turn
            if 0 in graph[mouse]: return 1
            for m in graph[mouse]:
                options.add(helper(turn - 1, cat, m))
                if 1 in options: return 1
            return min(options)

        n = len(graph) + (len(graph)&1)
        graph = {i: set(g) for i,g in enumerate(graph)}
        return helper(n, 2, 1)
