```
class Solution {
    int MOUSE = 1;
    int CAT = 2;
    int DRAW = 0;
    int len;
    public int catMouseGame(int[][] graph) {
        len = graph.length;
        return dfs(1,2,0,graph,new Integer[len][len][2*len]);
    }

    public int dfs(int m,int c,int turn,int[][] graph,Integer[][][] dp) {


        if(m==0) return MOUSE;
        if(m==c) return CAT;

        if(turn >= 2*len) return DRAW;

        if(dp[m][c][turn] != null) return dp[m][c][turn];

        //MOUSE turn
        if(turn%2 == 0) {

            boolean isThereDrawState = false;
            for(int neigh:graph[m]) {
                int temp = dfs(neigh,c,turn+1,graph,dp);
                if(temp == MOUSE) {
                    dp[m][c][turn] = MOUSE;
                    return MOUSE;
                } else if(temp == DRAW) {
                    isThereDrawState = true;
                }
            } 

            if(isThereDrawState)  {
                dp[m][c][turn] = DRAW;
                return DRAW;
            }
            else  {
                dp[m][c][turn] = CAT;
                return CAT;
            }
        } else {
            boolean isThereDrawState = false;
            for(int neigh:graph[c]) {
                if(neigh == 0) continue;
                int temp = dfs(m,neigh,turn+1,graph,dp);
                if(temp == CAT)  {
                    dp[m][c][turn] = CAT;
                    return CAT;
                }
                else if(temp == DRAW) isThereDrawState = true;
            }
            if(isThereDrawState)  {
                dp[m][c][turn] = DRAW;
                return DRAW;
            }
            else  {
                dp[m][c][turn] = MOUSE;
                return MOUSE;
            }
        }
    }
}
