class Solution:
    def catMouseGame(self, graph):
        N = len(graph)
        state = (1, 2, 0) # not in the initial queue
        Mouse, Cat, Draw = 1, 2, 0
        hashmap = defaultdict(int) # store the final result and deduplicate
        q = deque()
        q2 = deque()
        for n in range(1, N):
            hashmap[n,n,0] = Cat
            hashmap[n,n,1] = Cat
            hashmap[0,n,0] = Mouse
            hashmap[0,n,1] = Mouse
            q.append((0, n, 0)) # Mouse nodes
            q.append((0, n, 1)) # Mouse nodes
            q2.append((n, n, 0)) # Cat nodes
            q2.append((n, n, 1)) # Cat nodes

        def nei(vertex, ismouse):
            return tuple(v for v in graph[vertex] if ismouse or v != 0)

        indeg = defaultdict(int) # used for search of Mouse nodes
        for x in range(N):
            for y in range(1, N):
                t = 1
                indeg[x, y, t] = len(nei(y, False))

        indeg2 = defaultdict(int) # used for search of Cat nodes
        for x in range(N):
            for y in range(1, N):
                t = 0
                indeg2[x, y, t] = len(nei(x, True))

		# Search for Mouse nodes
        while q:
            qsize = len(q)
            for _ in range(qsize):
                x, y, t = q.popleft()

                if t == 1:
                    # x movement t=0 neighbors
                    for x_next in nei(x, True):
                        node = (x_next, y, 0)
                        if hashmap[node] == Draw:
                            hashmap[node] = Mouse
                            q.append(node)
                            if state == node:
                                return Mouse

                elif t == 0:
                    # y movement t=1 neighbors
                    for y_next in nei(y, False):
                        node = (x, y_next, 1)
                        indeg[node] -= 1
                        if indeg[node] == 0 and hashmap[node] == Draw:
                            hashmap[node] = Mouse
                            q.append(node)
                            if state == node:
                                return Mouse

		# Search for Cat nodes.
        q = q2
        indeg = indeg2
        while q:
            qsize = len(q)
            for _ in range(qsize):
                x, y, t = q.popleft()

                if t == 0:
                    # y movement t=1 neighbors
                    for y_next in nei(y, False):
                        node = (x, y_next, 1)
                        if hashmap[node] == Draw:
                            hashmap[node] = Cat
                            q.append(node) 
                            if state == node:
                                return Cat

                elif t == 1:
                    # x movement t=0 neighbors
                    for x_next in nei(x, True):
                        node = (x_next, y, 0)
                        indeg[node] -= 1
                        if indeg[node] == 0 and hashmap[node] == Draw:
                            hashmap[node] = Cat
                            q.append(node)
                            if state == node:
                                return Cat

        return Draw
