class Solution {
   private static int DRAW = 1, MOUSE_WIN = 2, CAT_WIN = 3;
    public int catMouseGame(int[][] graph) {
        return dfs(new int[graph.length+1][graph.length][graph.length], 0, graph, 1, 2)-1;
    }

    public int dfs(int[][][] alltable, int level, int[][] graph, int mousepos, int catpos) {
        if(level == graph.length + 1) {
            return DRAW;
        }

        if(alltable[level][mousepos][catpos] != 0) return alltable[level][mousepos][catpos];
        if(catpos == mousepos) {
            alltable[level][mousepos][catpos] = CAT_WIN;
            return CAT_WIN;
        }
        if(mousepos == 0) {
            alltable[level][mousepos][catpos] = MOUSE_WIN;
            return MOUSE_WIN;
        }
        if(level%2 == 0) {
            boolean hasdraw = false;
            for(int next: graph[mousepos]) {
                int oneret = dfs(alltable, level+1, graph, next, catpos);
                if(oneret == MOUSE_WIN) {
                    alltable[level][mousepos][catpos] = MOUSE_WIN;
                    return MOUSE_WIN;
                }
                else if(oneret == DRAW) {
                    hasdraw = true;
                }
            }
            int ret = hasdraw ? DRAW : CAT_WIN;
            alltable[level][mousepos][catpos] = ret;
            return ret;
