```
class Solution {
  Map<Integer, Set<Integer>> edge = new HashMap<>();
  Set<String> visitedStates = new HashSet<>();
  Map<String, Integer> sol = new HashMap<>();

  public int catMouseGame(int[][] graph) {
    for (int i = 0; i < graph.length; i++) {
      for (int to : graph[i]) {
        if (to != i) {
          edge.computeIfAbsent(i, k -> new HashSet<>()).add(to);
          edge.computeIfAbsent(to, k -> new HashSet<>()).add(i);
        }
      }
    }

    int ans = max(/* catNode= */ 2, /* mouseNode= */ 1);
    return ans == -1 ? 2 : ans;
  }

  // Mouse Move
  int max(int catNode, int mouseNode) {
    if (edge.get(mouseNode).contains(0)) return 1;

    String key = catNode + "_mouse_" + mouseNode;
    if (!visitedStates.add(key)) return 0;
    if (sol.containsKey(key)) {
      visitedStates.remove(key);
      return sol.get(key);
    }

    int max = Integer.MIN_VALUE;
    for (int i : edge.get(mouseNode)) {
      max = Math.max(max, min(catNode, i));
      if (max == 1) break;
    }

    visitedStates.remove(key);
    sol.put(key, max);
    return max;
  }

  // Cat Move
  int min(int catNode, int mouseNode) {
    if (catNode == mouseNode) return -1;
    if (edge.get(catNode).contains(mouseNode)) return -1;

    String key = catNode + "_cat_" + mouseNode;
    if (!visitedStates.add(key)) return 0;
    if (sol.containsKey(key)) {
      visitedStates.remove(key);
      return sol.get(key);
    }

    int min = Integer.MAX_VALUE;
    for (int i : edge.get(catNode)) {
      if (i == 0) continue;
      min = Math.min(min, max(i, mouseNode));
      if (min == -1) break;
    }

    visitedStates.remove(key);
    sol.put(key, min);
    return min;
  }
}
```

