class Solution {
public:

   int dp[501][51][51];
	int solver(int t,int m,int c,vector<vector<int>>&graph,int n){

    if(t==5*n) 
        dp[t][m][c]=0;

    if(m==0) 
        return dp[t][m][c]=1;

    if(m==c) 
        return dp[t][m][c]=2;



    if(dp[t][m][c]!=-1)
        return dp[t][m][c];

    int turn=t%2;

    if(turn==0){

        int ans[3];
        memset(ans,0,sizeof(ans));



        for(int i=0;i<graph[m].size();i++){

            int win=solver(t+1,graph[m][i],c,graph,n);

            ans[win]++;

            if(ans[1])
            return dp[t][m][c]=1;

        }


        if(ans[0])
            return dp[t][m][c]=0;
        else
            return dp[t][m][c]=2;


    }
    else
    {

        int ans[3];
        memset(ans,0,sizeof(ans));


        for(int i=0;i<graph[c].size();i++)
        {
            if(graph[c][i]!=0)
            {

                int win=solver(t+1,m,graph[c][i],graph,n);

                ans[win]++;

               if(ans[2])
                  return dp[t][m][c]=2;
            }
        }


        if(ans[0])
            return dp[t][m][c]=0;
        else
             return dp[t][m][c]=1;

    }

}
int catMouseGame(vector<vector<int>>& graph) {
    int n=graph.size();
    memset(dp,-1,sizeof(dp));
    return solver(0,1,2,graph,n);
}
};
