```
class Solution {
private:
     const int DRAW = 0;
     const int MOUSE = 1;
     const int CAT = 2;
public:
    int catMouseGame(vector<vector<int>>& graph) {
        int n = graph.size();
        // mouse, cat, turn
        int res[n][n][3]; // 3 just to make results and turns consistent
        int deg[n][n][3];
        memset(res, DRAW, sizeof(res)); // mark all states neutral as FINALLY DRAWN STATEs are never pushed in the queue
                                        // and thus all unpushed states will be FINALLY DRAWN
        for (int mouse = 0; mouse < n; mouse++) {
            for (int cat = 1; cat < n; cat++) {
                deg[mouse][cat][MOUSE] = (int)graph[mouse].size(); // no of children of this state, where all can we go from this state
                deg[mouse][cat][CAT] = (int)graph[cat].size(); // no of children of this state, where all can we go from this state
                for (auto &x : graph[cat]) {
                    if (x == 0) {
                        deg[mouse][cat][CAT]--; // note we can't go to 0 from some node for cat
                        break;
                    }
                }
            }
        }
        queue<tuple<int, int, int>>q;
        for (int both = 1; both < n; both++) {
            res[both][both][CAT] = CAT; // both are at the same node, cat wins
            res[both][both][MOUSE] = CAT; // both are at the same node, cat wins
            q.push({both, both, CAT});
            q.push({both, both, MOUSE});
        }
        for (int cat = 1; cat < n; cat++) {
            res[0][cat][CAT] = MOUSE; // mouse is at 0 mouse wins
            q.push(make_tuple(0, cat, CAT));
        }
        while (!q.empty()) {
            auto [mouse, cat, turn] = q.front();
            int curRes = res[mouse][cat][turn];
            assert(curRes != DRAW); // we NEVER push in a FINALLY DRAWN STATE to the queue
            // cout << mouse << " " << cat << " " << turn << " " << curRes << endl;
            q.pop();
            if (turn == MOUSE) {
                for (auto &neigh : graph[cat]) { // all nodes from where we can arrive at the popped node, 
                                                 // as the present turn is MOUSE the previous turn must have been CAT, 
                                                 // therefore cat made a move from some node to 'cat'
                    if (neigh == 0) continue; // not possible for cat to have come from 0
                    if (res[mouse][neigh][CAT] == DRAW) { // we try to resolve the unresolved states
                        if (curRes == CAT) {
                            res[mouse][neigh][CAT] = CAT;
                            q.push(make_tuple(mouse, neigh, CAT));
                        } else {
                            // because we are not pushing any node in the queue for which curRes is DRAW
                            // that means all the children which subtracted the degree must have been the loosing states
                            // if there are some FINALLY DRAWN STATES still attached to this node that means the degree will never become 0
                            deg[mouse][neigh][CAT]--;
                            if (!deg[mouse][neigh][CAT]) {
                                res[mouse][neigh][CAT] = MOUSE;
                                q.push(make_tuple(mouse, neigh, CAT));
                            }

                        }
                    }
                } 
            }
            else {
                for (auto &neigh : graph[mouse]) { // all nodes from where we can arrive at the popped node
                    if (res[neigh][cat][MOUSE] == DRAW) {
                        if (curRes == MOUSE) {
                            res[neigh][cat][MOUSE] = MOUSE;
                            q.push(make_tuple(neigh, cat, MOUSE));
                        } else {
                            deg[neigh][cat][MOUSE]--;
                            if (!deg[neigh][cat][MOUSE]) {
                                res[neigh][cat][MOUSE] = CAT;
                                q.push(make_tuple(neigh, cat, MOUSE));
                            }

                        }
                    }
                }

            }   
        }
        return res[1][2][MOUSE];
    }
};
