class Solution:
    def catMouseGame(self, G: List[List[int]]) -> int:

        n = len(G)

        START = (1, 2, True)

        # state = (mouse_pos, cat_pos, turn)
        degree = Counter()
        inv = defaultdict(list)
        g = defaultdict(list)

        q = []

        for mouse in range(n):
            # cat cannot be on hole
            for cat in range(1,n):
                for mouse_turn in True,False:
                    state = (mouse, cat, mouse_turn)

                    # check terminal
                    if mouse == 0 or mouse == cat:
                        q.append(state)

                    # register transitions
                    if mouse_turn:
                        # cat cannot transition from hole
                        predecessors = [(mouse,v,False) for v in G[cat] if v]
                    else:
                        predecessors = [(u,cat, True) for u in G[mouse]]

                    for pred in predecessors:
                        degree[pred] += 1
                        inv[state].append(pred)
                        g[pred].append(state)


        # state -> result
        ret = {}

        # process states
        for state in q:
            mouse,cat,mouse_turn = state

            if state in ret:
                continue

            # resolve state
            if mouse == 0:
                ret[state] = mouse_turn
            elif mouse == cat:
                ret[state] = not mouse_turn
            else:
                ret[state] = any(succ in ret and not ret[succ] for succ in g[state])

            # propagate resolution
            if not ret[state]:
                q.extend(inv[state])
            else:
                for pred in inv[state]:
                    degree[pred] -= 1

                    if degree[pred] == 0:
                        q.append(pred)


        return 0 if START not in ret else 1 if ret[START] else 2
