class Solution:
    def catMouseGame(self, graph: List[List[int]]) -> int:
        """
        Given
        Mouse starts at node 1
        Cat starts t node 2

        Graph, no ring, bi-direction..

        Brainstorm
        1. minimax algorithm. mouse takes the action to maximize the output, cat takes the action to minimize the output
            state: i, j (location of mouse, location of cat)
            rewards, 1 mouse win, -1 cat win, 0 draw

        2. use visited (set) to record visited state 
        """

        @lru_cache(None)
        def step_forward(i, j, flag_mouse_move=True, turn=0):
            if i == 0:
                return 1
            if i == j:
                return -1
            if turn >= 50:
                return 0

            if flag_mouse_move:
                output = -1
                for i_next in graph[i]:
                    output = max(output, step_forward(i_next, j, False, turn + 1))
                    if output == 1:
                        return output
                return output
            else:
                output = 1
                for j_next in graph[j]:
                    if j_next != 0:
                        output = min(output, step_forward(i, j_next, True, turn + 1))
                        if output == -1:
                            return output
                return output

        op = step_forward(1, 2, True)
        return op if op >= 0 else 2
