class Solution {
    public int catMouseGame(int[][] graph) {
        int n = graph.length;
        int [][][][] dp = new int[5*n][n][n][2];
        for(int i = 0; i<n; i++){
            dp[0][0][i] = new int[]{1, 1};
        }
        for(int i = 0; i<n; i++){
            dp[0][i][0] = new int[]{1, 1};
        }
        for(int i = 1; i<n; i++){
            dp[0][i][i] = new int[]{2, 2};
        }
        for(int i = 1; i<5*n; i++){
            for(int j = 0; j<n; j++){
                for(int k = 0; k<n; k++){
                    if(j==0 || k==0){
                        dp[i][j][k][0] = 1;
                        continue;
                    }
                    if(j==k){
                        dp[i][j][k][0] = 2;
                        continue;
                    }
                    int zero = 0;
                    int one = 0;
                    for(int v : graph[j]){
                        if(dp[i-1][v][k][1]==0)    zero++;
                        if(dp[i-1][v][k][1]==1)    one++;
                    }
                    if(one>0)   dp[i][j][k][0] = 1;
                    else if(zero==0)    dp[i][j][k][0] = 2;
                }
                for(int k = 0; k<n; k++){
                    if(j==0 || k==0){
                        dp[i][k][j][1] = 1;
                        continue;
                    }
                    if(j==k){
                        dp[i][k][j][1] = 2;
                        continue;
                    }
                    int zero = 0;
                    int two = 0;
                    for(int v : graph[j]){
                        if(dp[i-1][k][v][0]==0)    zero++;
                        if(dp[i-1][k][v][0]==2)    two++;
                    }
                    if(two>0)   dp[i][k][j][1] = 2;
                    else if(zero==0)    dp[i][k][j][1] = 1;
                }
            }
        }
        for(int i = 1; i<5*n; i++){
            if(dp[i][1][2][0]!=0)   return dp[i][1][2][0];
        }
        return 0;
    }
}
