                    outdegree[0][v][cat] -= 1;
                    if outdegree[0][v][cat] == 0 {
                        dp[0][v][cat] = 2;
                        q.push_back((0, v, cat));
                    }
                }
            }
        }
        dp[0][1][2]
    }
