# Code
```
class Solution {
public:
    int catMouseGame(vector<vector<int>>& graph) {
        // this is a games in graph kinda problem
        // we can solve this problem using BFS/DFS on the state space
        // we have to do reverse BFS/DFS from the states for which we know the answer already

        // turn
        const int CAT_TURN = 2;
        const int MOUSE_TURN = 1;

        // colors (or) the result of a state
        const int DRAW = 0;
        const int CAT = 2;
        const int MOUSE = 1;

        int n = graph.size();

        // the color or the result of a state (or node in the state space)
        int color[n][n][3];
        // all the states initially have the color DRAW
        memset(color, DRAW, sizeof color);

        // the states for which we already know the result
        vector<vector<int>> final_states;

        // find the states for which we already know the answer
        for(int i=1; i<n; i++) {
            for(int turn : {CAT_TURN, MOUSE_TURN}) {
                final_states.push_back({0, i, turn});
                color[0][i][turn] = MOUSE;

                final_states.push_back({i, i, turn});
                color[i][i][turn] = CAT;
            }
        }

        // find the degree of each state
        int degree[n][n][3];
        memset(degree, 0, sizeof degree);

        for(int i=0; i<n; i++) {
            for(int j=1; j<n; j++) {
                degree[i][j][MOUSE_TURN] = graph[i].size();
                degree[i][j][CAT_TURN] = graph[j].size() - (find(graph[j].begin(), graph[j].end(), 0) != graph[j].end());
            }
        }

        // function for finding the parent states of a given state
        function<vector<vector<int>>(vector<int>&)> parent_states = [&](vector<int>& state) {
            int i=state[0], j=state[1], t=state[2];
            vector<vector<int>> states;

            if(t == MOUSE_TURN) {
                for(int k : graph[j]) {
                    if(k != 0)
                        states.push_back({i, k, CAT_TURN});
                }
            }
            else {
                for(int k : graph[i]) {
                    states.push_back({k, j, MOUSE_TURN});
                }
            }

            return states;
        };

        // function for doing reverse DFS from the given state
        function<void(vector<int>&)> solve = [&](vector<int>& curr_state) {
            int i=curr_state[0], j=curr_state[1], t=curr_state[2];
            int c = color[i][j][t];

            // go through the parent states of the current state
            for(vector<int>& parent_state : parent_states(curr_state)) {
                int ip=parent_state[0], jp=parent_state[1], tp=parent_state[2];
                int cp = color[ip][jp][tp];

                // if the parent node hasn't been colored yet (or the result is still undefined, i.e DRAW)
                if(cp == DRAW) {
                    // if parent state's turn is same as color of current state
                    if(tp == c) {
                        color[ip][jp][tp] = c;
                        // do reverse from this node now
                        solve(parent_state);
                    }
                    else {
                        // decrement the degree of the parent state
                        degree[ip][jp][tp]--;

                        if(degree[ip][jp][tp] == 0) {
                            // color the parent node with same color as current state
                            // as there in no way for the current node to win
                            color[ip][jp][tp] = c;
                            // do reverse DFS from the parent node now
                            solve(parent_state);
                        }
                    }
                }
            }
        }; 

        for(vector<int>& state : final_states) {
            solve(state);
        }

        return color[1][2][MOUSE_TURN];
    }
};
