class Solution {
public:
    //Three 3 dp
    int visited[51][51][2][501];
    vector<vector<int>>graph;
    int draw = 0;
    int cwin = 2;
    int rwin = 1;
    int find(int cat,int mouse,bool mousemove,int step){ 
        if(mouse==cat) {
            return cwin;
        }

        if(mouse==0) return rwin;
        if(step>200) return draw;



         if(visited[mouse][cat][mousemove][step]!=-1) return  visited[mouse][cat][mousemove][step];

        if(mousemove){
            //Mouse will want to win
            bool mouseWins = false;
            bool catWins = false;
            bool isDraw = false;
            for(auto &child : graph[mouse]){
                int ans = find(cat,child,false,step+1);
                if(ans==rwin) {
                    mouseWins = true;
                    break;
                }
                if(ans==cwin) catWins = true;
                if(ans==draw) isDraw = true;
            }
            if(mouseWins) return visited[mouse][cat][mousemove][step]  = rwin;
            if(isDraw) return  visited[mouse][cat][mousemove][step]  = draw;
            return  visited[mouse][cat][mousemove][step]  = cwin;
