class Solution(object):
    def catMouseGame(self, graph):
        MOUSE, CAT, DRAW = 1, 2, 0
        N = len(graph)

        def get_parents(mouse, cat, turn):
            if turn == CAT:
                for mouse_prev in graph[mouse]:
                    yield mouse_prev, cat, MOUSE
            else:
                for cat_prev in graph[cat]:
                    if cat_prev != 0:
                        yield mouse, cat_prev, CAT

        degree = dict()
        """
        Record all the ways the current state can move, and only admit the lose when there is no more move current
        state can make. So that's why we only subtract the degree if we can not win in the BFS while loop
        """
        for m in range(N):
            for c in range(N):
                degree[m, c, 1] = len(graph[m])
                degree[m, c, 2] = len(graph[c]) - (0 in graph[c])

        colors = collections.defaultdict(int)
        """
        color = {
            (mouse_position, cat_position, whose_turn): winner
        } 
        """

        queue = collections.deque([])
        """
        Push all the win cases into the queue

        queue = [(mouse_position, cat_position, whose_turn, winner)]
        """
        for turn in [MOUSE, CAT]:
            for i in range(N):
                colors[0, i, turn] = MOUSE
                queue.append((0, i, turn, MOUSE))
                if i > 0:
                    colors[i, i, turn] = CAT
                    queue.append((i, i, turn, CAT))

        while queue:
            mouse, cat, turn, color = queue.popleft()
            for mouse_prev, cat_prev, turn_prev in get_parents(mouse, cat, turn):
                if colors[mouse_prev, cat_prev, turn_prev] == DRAW:
                    if color == turn_prev:
                        """Previous state can make a win move, then it will definitely make the win move,
                        so color the state and add this state into the queue
                        """
                        colors[mouse_prev, cat_prev, turn_prev] = color
                        queue.append((mouse_prev, cat_prev, turn_prev, color))
                    else:
                        degree[mouse_prev, cat_prev, turn_prev] -= 1
                        if degree[mouse_prev, cat_prev, turn_prev] == 0:
                            colors[mouse_prev, cat_prev, turn_prev] = 3 - turn_prev
                            queue.append((mouse_prev, cat_prev, turn_prev, 3 - turn_prev))

        return colors[1, 2, 1]
