class Solution {
    static HashMap<String, Integer> memo = new HashMap<>(); 
    static int WIN = 1, LOSE = 2, DRAW = 0; // with respect to mouse
    public int catMouseGame(int[][] graph) {
        memo.clear();
        int mousePosition = 1, catPosition = 2, hole = 0, totalMoves = 0;
        return playRecurse(graph, mousePosition, catPosition, hole, totalMoves);
    }
    private static int playRecurse(int[][] graph, int mousePosition, int catPosition, int hole, int totalMoves) {
        if (totalMoves > 2 * graph.length) { 
            // all nodes are visited, now if cat or mouse visit a node it would mean that they are repeating again and again to WIN.
			return DRAW;
        }
        if (mousePosition == catPosition) {
            return LOSE;
        }
        if (mousePosition == hole) {
            return WIN;
        }

        String situation = mousePosition + " " + catPosition + " " + totalMoves;

        if (memo.containsKey(situation)) {
            return memo.get(situation);
        }

        if (totalMoves % 2 == 0) { // mouse turn
            // to check if we can draw the game, better than a lose
			boolean canDraw = false;

            for (int adjacentNode : graph[mousePosition]) {
                int playResult = playRecurse(graph, adjacentNode, catPosition, hole, totalMoves + 1);

                if (playResult == WIN) { // mouse wins
                    memo.put(situation, WIN);
                    return WIN;
                }

                if (playResult == DRAW) {
                    canDraw = true;
                }
            }

            if (canDraw) {
                memo.put(situation, DRAW);
                return DRAW;
            }

            memo.put(situation, LOSE);
            return LOSE; // cat wins
        }

        // cat turn
        boolean canDraw = false;

        for (int adjacentNode : graph[catPosition]) {
            if (adjacentNode == hole) {
                continue; // cat cannot travel to the Hole
            }

            int playResult = playRecurse(graph, mousePosition, adjacentNode, hole, totalMoves + 1);

            if (playResult == LOSE) {
                memo.put(situation, LOSE);
                return LOSE; // cat wins
            }

            if (playResult == DRAW) {
                canDraw = true;
            }
        }

        if (canDraw) {
            memo.put(situation, DRAW);
            return DRAW;
        }

        memo.put(situation, WIN);
        return WIN; // mouse wins
    }
}

