var catMouseGame = function(graph) {
	// calculating the possible maximum turns, and because there are only two game players(mouse, cat),
	// we just multiply by 2 of the graph size
    const size = graph.length * 2 
    const cache = Array(size).fill(-1).map(a => Array(graph.length).fill(-1).map(aa => Array(graph.length).fill(-1)));

    const check = (turn, x, y) => {
        if (turn >= size) return 0;
        if (x === y) return 2;
        if (x === 0) return 1;
        if (cache[turn][x][y] > -1) {
            return cache[turn][x][y];
        }
        if (turn % 2 === 0) { // Mouse turn
            let vals = [];
            for (let v of graph[x]) { // gathering all the result for mouse
                vals.push(check(turn+1, v, y));
            }
            if (vals.some(v => v === 1)) { // if any result returned 1, mouse win
                cache[turn][x][y] = 1;
                return 1;
            }
            if (vals.every(v => v === 2)) { // if all(every) result returned 2, cat win
                cache[turn][x][y] = 2;
                return 2;
            }
            cache[turn][x][y] = 0;
            return 0; // If it is not the above case, it is draw

        } else { // Cat turn
            let vals = [];
            for (let v of graph[y]) { // gathering all the result for cat
                if (v !== 0) {
                    vals.push(check(turn+1, x, v));
                }
            }
            if (vals.some(v => v === 2)) { // if any result returned 2, cat win
                cache[turn][x][y] = 2;
                return 2;
            }
            if (vals.every(v => v === 1)) { // if all(every) result returned 1, mouse win
                cache[turn][x][y] = 1;
                return 1;
            }
            cache[turn][x][y] = 0;
            return 0; // if it is not the above case, it is draw
        }
    };
    return check(0,1,2)
};

