var catMouseGame = function(graph) {
    let n = graph.length,
        dp = Array.from({ length: n }, _ =>
            Array.from({ length: n }, _ => Array(2 * n))
        );

    // 0 when mouse starts, 1 when cat starts

    const backTracking = (mouseIdx, catIdx, moves) => {
        if (mouseIdx === 0)
            return 1;
        if (mouseIdx === catIdx)
            return 2;
        if (moves >= n * 2)
            return 0;

        if (dp[mouseIdx][catIdx][moves] === undefined) {
            let nextMouseIdx = mouseIdx,
                nextCatIdx = catIdx,
                chance = 2;

            for (const each of graph[(moves % 2) ? catIdx : mouseIdx]) {
                (moves % 2) ? nextCatIdx = each : nextMouseIdx = each;
                if (!nextCatIdx)
                    continue;

                const value = backTracking(nextMouseIdx, nextCatIdx, moves + 1);
                if (value === (moves % 2) + 1)
                    return dp[mouseIdx][catIdx][moves] = value;

                chance = Math.min(chance, value);
            }
            dp[mouseIdx][catIdx][moves] = chance;
        }
        return dp[mouseIdx][catIdx][moves];
    }

    return backTracking(1, 2, 0);
};

