class Solution {
    public int catMouseGame(int[][] graph) {
        int n = graph.length;
        int[][][] dp = new int[2 * n][n][n];
        for (int[][] nums : dp) {
            for (int[] arr : nums) {
                Arrays.fill(arr, -1);
            }
        }
        return dfs(graph, 0, 1, 2, dp);
    }
    //假设图中有n个结点，不论是猫还是老鼠，当各自走完了n个结点时还没有分出胜负，则表示平局，若一人走一步，则最多有 2n 步。这样的话每一个状态实际上是由三个因素组成的：当前步数，老鼠所在结点，和猫所在结点。 https://www.cnblogs.com/grandyang/p/11515655.html
    // t: steps, x: mouse position, y: cat position
    public int dfs(int[][] graph, int t, int x, int y, int[][][] dp) {
        if (t == graph.length * 2) return 0;
        if (x == y) return 2;
        if (x == 0) return 1;
        if (dp[t][x][y] != -1) return dp[t][x][y];
        boolean mouseTurn = (t % 2) == 0;
        if (mouseTurn) {
            boolean catWin = true;
            for (int p : graph[x]) {
                int next = dfs(graph, t + 1, p, y, dp);
                if (next == 1) {
                    return dp[t][x][y] = 1;
                }
                if (next == 0) {
                    catWin = false;
                }
            }
            if (catWin) {
                return dp[t][x][y] = 2;
            } else {
                return dp[t][x][y] = 0;
            }
        } else {
            boolean mouseWin = true;
            for (int p : graph[y]) {
                if (p == 0) continue;
                int next = dfs(graph, t + 1, x, p, dp);
                if (next == 2) {
                    return dp[t][x][y] = 2;
                }
                if (next == 0) {
                    mouseWin = false;
                }
            }
            if (mouseWin) {
                return dp[t][x][y] = 1;
            } else {
                return dp[t][x][y] = 0;
            }
        }
    }
}
