class Solution {
public:
    int dp[105][52][52];

    int solve(vector<vector<int>> &g , int steps , int mouseposition , int catposition)
    {
        int n = g.size();
        if(steps >= 2*n) return dp[steps][mouseposition][catposition] = 0;
        if(mouseposition==catposition) return dp[steps][mouseposition][catposition] = 2;
        if(mouseposition==0) return dp[steps][mouseposition][catposition] = 1;
        if(dp[steps][mouseposition][catposition] != -1) return dp[steps][mouseposition][catposition];
        int turn = steps%2;
        if(turn == 0){
            bool flag = true;
            for(int i = 0 ; i < g[mouseposition].size() ; ++i){
                int next = solve(g , steps + 1 , g[mouseposition][i] , catposition);
                if(next == 1) return dp[steps][mouseposition][catposition] = 1;
                else if(next != 2) flag = false;
            }
            if(flag) return dp[steps][mouseposition][catposition] = 2;
            else return dp[steps][mouseposition][catposition] = 0;
        }
        else if(turn == 1){
            bool flag = true;
            for(int i = 0 ; i < g[catposition].size() ; ++i){
                if(g[catposition][i] != 0){
                  int next = solve(g , steps + 1 , mouseposition , g[catposition][i]) ;
                if(next == 2) return dp[steps][mouseposition][catposition] = 2;
                else if(next != 1) flag = false;
                }
            }
            if(flag) return dp[steps][mouseposition][catposition] = 1;
            else return dp[steps][mouseposition][catposition] = 0;
        }
        return dp[steps][mouseposition][catposition];
    }

    int catMouseGame(vector<vector<int>>& graph) {
        memset(dp,-1,sizeof dp);
        int n = graph.size();
        return solve(graph,0,1,2);
    }
};

