## Brute Force
```
class Solution {

    private static final int DRAW = 0, MOUSE_WIN = 1, CAT_WIN = 2;

    public int catMouseGame(int[][] graph) {
        return catMouseGameFrom(graph, 0, 1, 2);
    }

    private int catMouseGameFrom(int[][] graph, int turn, int mousePos, int catPos) {
        if (turn == graph.length * 2) {
            return DRAW;
        } else if (mousePos == 0) {
            return MOUSE_WIN;
        } else if (mousePos == catPos) {
            return CAT_WIN;
        }

        if (turn % 2 == 0) { // Mouse's turn
            boolean catWin = true;
            for (int mouseTo : graph[mousePos]) {
                int res = catMouseGameFrom(graph, turn + 1, mouseTo, catPos);
                if (res == MOUSE_WIN) {
                    return MOUSE_WIN;
                } else if (res == DRAW) {
                    catWin = false;
                }
            }
            if (catWin) {
                return CAT_WIN;
            } else {
                return DRAW;
            }
        } else { // Cat's turn
            boolean mouseWin = true;
            for (int catTo : graph[catPos]) {
                if (catTo != 0) {
                    int res = catMouseGameFrom(graph, turn + 1, mousePos, catTo);
                    if (res == CAT_WIN) {
                        return CAT_WIN;
                    } else if (res == DRAW) {
                        mouseWin = false;
                    }
                }
            }
            if (mouseWin) {
                return MOUSE_WIN;
            } else {
                return DRAW;
            }
        }
    }
}
