    def catMouseGame(self, graph):
        """
        :type graph: List[List[int]]
        :rtype: int
        """
        dp = {}

        v = set()

        # turn 1 means mouse, 0 means cat
        # 0 is loss, 1 is win and 2 is draw
        def dfs(ci,mi,turn):

            # cat lost
            if mi==0:
                dp[(ci,mi,turn)] = 1 if turn else 0
                return dp[(ci,mi,turn)]

            # cat won
            if mi==ci:
                dp[(ci,mi,turn)] = 0 if turn else 1
                return dp[(ci,mi,turn)]

            if (ci,mi,turn) in dp:
                return dp[(ci,mi,turn)]

            # if this node is already visited, its a draw.
            if (ci,mi,turn) in v:
                return 2

            v.add((ci,mi,turn))

            res_out = []

            if turn:
                for mii in graph[mi]:
                    res = dfs(ci,mii,(turn+1)%2)
                    # if there is alteast one losing node from this node, this is winning node
                    if res==0:
                        dp[(ci,mi,turn)] = 1
                        return dp[(ci,mi,turn)]
                    res_out.append(res)
            else:
                for cii in graph[ci]:
                    if cii==0:
                        continue
                    res = dfs(cii,mi,(turn+1)%2)
                    # if there is alteast one losing node from this node, this is winning node
                    if res==0:
                        dp[(ci,mi,turn)] = 1
                        v.remove((ci,mi,turn))
                        return dp[(ci,mi,turn)]
                    res_out.append(res)

            count = 0
            for res in res_out:
                count+=(1 if res==2 else 0)

            # if cat/mouse can make a draw move then it will be the priority else it is on losing position
            dp[(ci,mi,turn)] = 2 if count>0 else 0
            v.remove((ci,mi,turn))
            return dp[(ci,mi,turn)]

        res = dfs(2,1,1)

        if res==1:
            return 1
        if res==0:
            return 2
        return 0


