func catMouseGame(graph [][]int) int {
    n := len(graph)
    dp := make([][][]int, n*2)
    for i:= range dp {
        dp[i] = make([][]int, n)
        for j := range dp[i] {
            dp[i][j] = make([]int, n)
        }
    }
    fill(&dp)

    return getBoardState(&graph, &dp, 0, 1, 2)
}

func getBoardState(graph *[][]int, dp *[][][]int, turn int, rat int, cat int) int {
    n := len(*graph)
    if (turn == 2*n) {
        return 0
    }

    if (rat == cat) {
        (*dp)[turn][rat][cat] = 2
        return 2
    }

    if (rat == 0) {
        (*dp)[turn][rat][cat] = 1
        return 1
    }

    if ((*dp)[turn][rat][cat] != -1) {
        return (*dp)[turn][rat][cat]
    }

    ratTurn := (turn % 2) == 0

    if (ratTurn) {
        catCanWin := true
        for i := 0; i < len((*graph)[rat]); i++ {
            next := getBoardState(graph, dp, turn+1, (*graph)[rat][i], cat)
            if (next == 1) {
                (*dp)[turn][rat][cat] = 1
                return 1
            } else if (next != 2) {
                catCanWin = false
            }
        }

        if (catCanWin) {
            (*dp)[turn][rat][cat] = 2
            return 2
        } else {
            (*dp)[turn][rat][cat] = 0
            return 0
        }
    } else {
        ratCanWin := true
        for i := 0; i < len((*graph)[cat]); i++ {
            if ((*graph)[cat][i] == 0) {continue}
            next := getBoardState(graph, dp, turn+1, rat, (*graph)[cat][i])
            if (next == 2) {
                (*dp)[turn][rat][cat] = 2
                return 2
            } else if (next != 1) {
                ratCanWin = false
            }
        }

        if (ratCanWin) {
            (*dp)[turn][rat][cat] = 1
            return 1
        } else {
            (*dp)[turn][rat][cat] = 0
            return 0
        }
    }
}

func fill (dp *[][][]int) {

    for l,_ := range (*dp) {
        for m,_ := range (*dp)[l] {
            for n,_ := range (*dp)[l][m] {
                (*dp)[l][m][n] = -1
            }
        }
    }
}
