```Javascript
// implement Queue data structure
class QNode {
  constructor(val) {
    this.val = val;
    this.pred = this.succ = null;
  }
}

class Queue {
  constructor() {
    const front = new QNode();
    const end = new QNode();
    front.succ = end;
    end.pred = front;
    this.front = front;
    this.end = end;
  }
  empty() {
    return this.front.succ === this.end;
  }
  enqueue(val) {
    const newNode = new QNode(val);
    newNode.succ = this.end;
    this.end.pred.succ = newNode;
    this.end.pred = newNode;
  }
  dequeue() {
    const val = this.front.succ.val;
    const newFirstNode = this.front.succ.succ;
    newFirstNode.pred = this.front;
    this.front.succ = newFirstNode;
    return val;
  }
}


const countNonZero = xs => {
  for (let i = 0; i < xs.length; i++) {
    if (xs[i] === 0) return xs.length - 1;
  }
  return xs.length;
}

const DRAW = 0;
const MOUSE = 1;
const CAT = 2;

var catMouseGame = function(graph) {
  const N = graph.length;

  const graphMatrix = new Array(N).fill().map(
    (_, mousePos) => (new Array(N).fill().map(
      (_, catPos) => ({
        degree: {
          // now we are in (mousePos, catPos)
          [MOUSE]: graph[mousePos].length, // number of ways that mouse can move to from current pos if it's mouse's move
          [CAT]: countNonZero(graph[catPos]), // number of ways that cat can move to from current pos if it's cat's move
        },
        color: {
          [MOUSE]: DRAW, // the outcome if it's mouse's turn to move
          [CAT]: DRAW, // the outcome if it's cat's turn to move
        }
      }),
    ))
  );

  class Node {
    constructor(mousePos, catPos, whichTurn) {
      this.mousePos = mousePos;
      this.catPos = catPos;
      this.whichTurn = whichTurn; // which player moves now
    }
    getColor() {
      return graphMatrix[this.mousePos][this.catPos].color[this.whichTurn];
    }
    setColor(color) {
      graphMatrix[this.mousePos][this.catPos].color[this.whichTurn] = color;
    }
    decDegree() {
      graphMatrix[this.mousePos][this.catPos].degree[this.whichTurn]--;
    }
    getDegree() {
      return graphMatrix[this.mousePos][this.catPos].degree[this.whichTurn];
    }
    getParents() {
      const parents = [];
      switch (this.whichTurn) {
        case CAT: // then the previous turn would be mouse
          for (let prevMousePos of graph[this.mousePos]) {
            parents.push(new Node(prevMousePos, this.catPos, MOUSE));
          }
          break;
        case MOUSE: // then the previous turn would be cat
          for (let prevCatPos of graph[this.catPos]) {
            if (prevCatPos === 0) continue;
            parents.push(new Node(this.mousePos, prevCatPos, CAT));
          }
          break;
      }
      return parents;
    }
  }

  const queue = new Queue();
  for (let i = 1; i < N; i++) {
    for (let player of [MOUSE, CAT]) {
      const mouseWinNode = new Node(0, i, player);
      mouseWinNode.setColor(MOUSE);
      queue.enqueue(mouseWinNode);
	  const catWinNode = new Node(i, i, player);
      catWinNode.setColor(CAT);
	  queue.enqueue(catWinNode);
    }
  }

  // all nodes in queue are colored
  while (!queue.empty()) {
    const currNode = queue.dequeue();
    const color = currNode.getColor();

    for (let parent of currNode.getParents()) {
      if (parent.getColor() === DRAW) { // if it's uncolored
        if (parent.whichTurn === color) { // if we can decide the color
          parent.setColor(color);
          queue.enqueue(parent);
        } else { // we can't decide the color, but we can be sure that the parent node has one less chance to win
          parent.decDegree();
          if (parent.getDegree() === 0) { // loses if has no chance to win
            const failColor = parent.whichTurn === CAT ? MOUSE : CAT;
            parent.setColor(failColor);
            queue.enqueue(parent);
          }
        }
      }
    }
  }

  const startNode = new Node(1, 2, MOUSE);
  return startNode.getColor();
}
