class Solution(object):
    def catMouseGame(self, graph):
        N=len(graph)
        f=collections.defaultdict(int)

        q=collections.deque([])
        branchs={}
        MOUSE,CAT=1,2

        for i in xrange(1,N):
            for j in xrange(2):
                q.append((0,i,j,MOUSE))
                q.append((i,i,j,CAT))
                f[0,i,j]=MOUSE
                f[i,i,j]=CAT

        for i in xrange(N):
            for j in xrange(1,N):
                branchs[i,j,0]=len(graph[i])
                branchs[i,j,1]=len(graph[j])-(0 in graph[j])

        while q:
            mouse,cat,turn,winner=q.popleft()
            if turn==0:
                for i in graph[cat]:
                    if i==0: continue
                    if f[mouse,i,1]==0:#cat moves
                        if winner==CAT:
                            f[mouse,i,1]=CAT
                            q.append((mouse,i,1,CAT))
                        else:
                            branchs[mouse,i,1]-=1
                            if branchs[mouse,i,1]==0:
                                f[mouse,i,1]=MOUSE
                                q.append((mouse,i,1,MOUSE))
            else:
                for i in graph[mouse]:
                    if f[i,cat,0]==0:
                        if winner==MOUSE:
                            f[i,cat,0]=MOUSE
                            q.append((i,cat,0,MOUSE))
                        else:
                            branchs[i,cat,0]-=1
                            if branchs[i,cat,0]==0:
                                f[i,cat,0]=CAT
                                q.append((i,cat,0,CAT))

        return f[1,2,0]
