import java.util.*;

public class Solution {

  private enum Move {
    CAT, MOUSE
  }

  public int catMouseGame(int[][] graph) {

    int[][][] dp = new int[55][55][3];

    for (int i = 0; i < dp.length; i++) {
      for (int j = 0; j < dp[i].length; j++) {
        Arrays.fill(dp[i][j], -1);
      }
    }
    return solve(1, 2, 0, Move.CAT, dp, graph);
  }

  private int solve(int mouse, int cat, int turn, Move move, int[][][] dp, int[][] graph) {

    if (mouse == cat) {
      // it can not be 0.
      return 2;
    }

    if (mouse == 0) {
      return 1;
    }

    if (dp[mouse][cat][turn] != -1) {
      return dp[mouse][cat][turn];
    }

    dp[mouse][cat][turn] = 0;

    int player = move == Move.MOUSE ? mouse : cat;

    boolean catWin = false;
    boolean mouseWin = false;
    boolean draw = false;

    for (int i = 0; i < graph[player].length; i++) {

      if (move == Move.CAT && graph[cat][i] == 0) {
        continue;
      }

      int moveResult = solve(move == Move.MOUSE ? graph[player][i] : mouse,
          move == Move.CAT ? graph[player][i] : cat, turn ^ 1,
          move == Move.MOUSE ? Move.CAT : Move.MOUSE, dp, graph);

      catWin |= moveResult == 2;
      mouseWin |= moveResult == 1;
      draw |= moveResult == 0;
    }

    int ans;
    if (move == Move.MOUSE) {
      // mouse's turn
      ans = mouseWin ? 1 : draw ? 0 : 2;
    } else {
      ans = catWin ? 2 : draw ? 0 : 1;
    }

    return dp[mouse][cat][turn] = ans;
  }
}
