            states[0][i][0] = 1; //mouse win state
            states[0][i][1] = 1;
        }
        for (int i=1; i<N; i++) {
            states[i][i][0] = 2; //cat win
            states[i][i][1] = 2;
        }
        boolean progress = true;
        while (progress) {
