class Solution(object):
    def catMouseGame(self, graph):
        """
        :type graph: List[List[int]]
        :rtype: int
        """
        n = len(graph)
        dp = [[[-1, -1] for i in range(n)] for _ in range(n)]
        for i in range(n):
            dp[i][i][0] = 2
            dp[i][i][1] = 2
            dp[0][i][0] = 1
            dp[0][i][1] = 1

        def get_res(m, c, mt, path):
            if dp[m][c][mt] < 0:
                path.append((m, c, mt))
                dp[m][c][mt] = 0
                if mt:
                    res = 2
                    for nxt in graph[m]:
                        t = get_res(nxt, c, 0, path)
                        if t == 0:
                            res = 0
                        elif t == 1:
                            res = 1
                            break
                    dp[m][c][mt] = res
                else:
                    res = 1
                    for nxt in graph[c]:
                        if nxt != 0:
                            t = get_res(m, nxt, 1, path)
                            if t == 0:
                                res = 0
                            elif t == 2:
                                res = 2
                                break
                    dp[m][c][mt] = res
                path.pop()
            return dp[m][c][mt]
        return get_res(1, 2, 1, [])
