                int winner=-1;
                bool canMouseWin=false;
                bool canDrawHappen=false;
                for(auto pos:graph[mousepos])
                {
                    winner=dfs(graph,outcome,catpos,pos,moves+1,limit);
                    if(winner==1)
                    {
                        canMouseWin=true;
                        break;
                    }
                    else if(winner==0)
                        canDrawHappen=true;
                }
                if(canMouseWin)
                    outcome[catpos][mousepos][moves]=1;
                else if(canDrawHappen)
                    outcome[catpos][mousepos][moves]=0;
                else
                    outcome[catpos][mousepos][moves]=2;
            }
            else//cat turn
            {
                int winner=-1;
                bool canCatWin=false;
                bool canDrawHappen=false;
                for(auto pos:graph[catpos])
                {
                    if(pos==0)
                        continue;
                    winner=dfs(graph,outcome,pos,mousepos,moves+1,limit);
                    if(winner==2)
                    {
                        canCatWin=true;
