                   if(vis[nxt.x][nxt.y][nxt.turn]==-1&&nxt.y!=0)
                    {
                         if(vis[noob.x][noob.y][noob.turn]==2)
                        {
                        vis[nxt.x][nxt.y][nxt.turn]=2 ;
                        q.push(nxt) ;
