class Solution {
  public:
    string breakPalindrome(string palindrome) {
      set < char > s;
      int n = palindrome.size();
      if (n == 1) return "";
      for (int i = 0; i < (palindrome.size() + 1) / 2; i++) {
        s.insert(palindrome[i]);
      }
      if (s.size() == 1) {
        if (palindrome[0] == 'a') palindrome[n - 1] = 'b';
        else palindrome[0] = 'a';
        return palindrome;
      }
      int cnt = 0;
      for (int i = 0; i < palindrome.size(); i++) {
        if (palindrome[i] != 'a') {
          if (i != n / 2)
            palindrome[i] = 'a';
          else {
            for (int k = i + 1; k < n; k++) {
              if (palindrome[k] != 'a') {
                palindrome[n / 2] = 'a';
                cnt++;
                break;
              }
            }
            if (!cnt) palindrome[n - 1] = 'b';
          }
          break;
        }
      }
      return palindrome;
    }
};
