var breakPalindrome = function(palindrome) {
    if(palindrome.length === 1){
        return "";
    }
    let res = "";
    for(let i=0; i < (Math.floor(palindrome.length / 2));i++){
        if(palindrome[i] !== 'a'){
            res = palindrome.slice(0,i)  + 'a' + palindrome.slice(i+1,palindrome.length);
            break;
        }
    }
    if(res === ""){
        res = palindrome.slice(0,palindrome.length-1) + 'b';
    }
    return res;
};
