class Solution {
    public String breakPalindrome(String palindrome) {
        // edge case
        if (palindrome.length() == 1) return "";

        // find replacement index
        int swap_idx = -1;
        for (int i = 0; i < palindrome.length()/2; i++) {
            if (palindrome.charAt(i) != 'a') {
                swap_idx = i;
                break;
            }
        }

        StringBuilder res = new StringBuilder();

        // we found an index to replace
        if (swap_idx != -1) {
            res.append(palindrome.substring(0,swap_idx));
            res.append('a');
            res.append(palindrome.substring(swap_idx + 1));

        // we did not find an index to replace, replace the last one with 'b'
        } else {
            res.append(palindrome.substring(0, palindrome.length() - 1));
            res.append('b');
        }
        return res.toString();
    }
}
