class Solution {


    public String breakPalindrome(String s) {

        int  n = s.length();  //length of the String

        //If the length of the string is 1 then it always a palindrome we cannot 
		//modify it in order to make it non-palindrome with length 1
        if(n == 1) return "";  

        for(int i = 0;i<n;i++){

            int j = n - 1 - i;
            //whenever the length is odd we just skip
            if(i == j) {
                continue;
            }
            //If first character is not 'a' then replace it with 'a' and return s
            if(s.charAt(i)!='a'){ 
            char c[] = s.toCharArray();
                c[i] = 'a';
                s = new String(c);
              return s;
            }

        }
        char c[] = s.toCharArray(); 
        //If all characters are 'a' then last character should be replaced with 
        //'b' to get lexicographically smallest one
        c[n-1] = 'b';       
        s = new String(c);
        return s;
    }
}
//Time Complexity O(n)
