class Solution {
public:
    string breakPalindrome(string str) {

        int len = str.size();
        if(len == 1){
            str[0] == 'a';
            return "";
        }
        int c;
        if(len%2 == 0){
            c = -1;
        }else{
            c = len/2;
        }
        for(int i=0; i<len; i++){

            if(str[i] != 'a' and i != c){
                str[i] = 'a';
                return str;
            }
        }
        str[len-1] = 'b';
        return str;

    }
};
