    public String breakPalindrome(String s) {
        if(s.length()<=1) return "";
        int n = s.length(), mid = (n>>1)-(n&1);
        char[] arr = s.toCharArray();
        for(int i=0;i<=mid;i++){
            if(arr[i]!='a'){
                arr[i] = 'a';
                return new String(arr);
            }
        } 
        arr[n-1] = 'b';
        return new String(arr);
   }
}
