class Solution:
# @param {integer[]} nums
# @param {integer} target
# @return {integer}
def search(self, nums, target):
    if not nums:
        return -1
    return self.binarySearch(nums, target, 0, len(nums)-1)

def binarySearch(self, nums, target, start, end):
    if end < start:
        return -1
    mid = (start+end)/2
    if nums[mid] == target:
        return mid
    if nums[start] <= target < nums[mid]: # left side is sorted and has target
        return self.binarySearch(nums, target, start, mid-1)
    elif nums[mid] < target <= nums[end]: # right side is sorted and has target
        return self.binarySearch(nums, target, mid+1, end)
    elif nums[mid] > nums[end]: # right side is pivoted
        return self.binarySearch(nums, target, mid+1, end)
    else: # left side is pivoted
        return self.binarySearch(nums, target, start, mid-1)

```swift
class Solution {
    func search(_ n: [Int], _ t: Int) -> Int {
        var lhs = 0, rhs = n.count - 1
        while lhs <= rhs {
            let mid = (rhs - lhs) / 2 + lhs
            let val = (l: n[lhs], m: n[mid], r: n[rhs])
            if val.m == t { return mid }
            func setR() { rhs = mid - 1 }
            func setL() { lhs = mid + 1 }
            if val.m >= val.l {
                val.m > t && t >= val.l ? setR() : setL()
            } else {
                val.m < t && t <= val.r ? setL() : setR()
            }
        }
        return -1
    }
}
```

