    public int search(int[] nums, int target) {
        if (nums.length == 0) {
            return -1;
        }

        int left = 0;
        int right = nums.length - 1;

        while (left <= right) {
            int mid = left + (right - left) / 2;

            if (nums[mid] == target) {
                return mid;
            }

            if (nums[mid] < nums[right]) { // right half sorted
                if (nums[mid] < target && target <= nums[right]) { // target within right half
                    left = mid + 1; 
                } else {
                    right = mid - 1;
                }
            } else { // left half sorted
                if (nums[left] <= target && target < nums[mid]) { // target within left half
                    right = mid - 1;
                } else {
                    left = mid + 1;
                }
            }
        }

        return -1;
    }
