// search an element in a sorted and rotated array. O(logn)
int search(int nums[], int size, int target)
{
    // initialize low and high variables
    int low = 0, high = size - 1;

    // logic
    while (low <= high)
    {
        // compute mid
        int mid = (low + high) / 2;

        // check middle element (base case)
        if (nums[mid] == target)
            return mid;

        // left half is sorted
        if (nums[low] <= nums[mid])
        {
            if (target >= nums[low] && target < nums[mid]) // notice target <= nums[mid] is not required because equality has already been checked earlier
                high = mid - 1; // go to left half
            else
                low = mid + 1; // go to right half
        }
        // right half is sorted
        else
        {
            if (target > nums[mid] && target <= nums[high]) // notice target >= nums[mid] is not required because equality has already been checked earlier
                low = mid + 1; // go to right half
            else
                high = mid - 1; // go to left half
        }
    }

    // if element not found
    return -1;
}
